mapper.select:
select

from course
where id = #{id,jdbcType=INTEGER}
and user_id = #{userId,jdbcType=INTEGER}

select
	EXISTS(
			select *
			from
				course
			where
				  id = #{id,jdbcType=INTEGER}
			  and user_id = #{userId,jdbcType=INTEGER}
		)

mapper.update:
update
	course
set banned = #{banned,jdbcType=INTEGER}
where
	  id = #{id,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
